     $knopfoben .= "<a href=\"".$href."\" target=\"_new\" title=\"".JTEXT::_('SEM_1048')."\" border=\"0\">".JHTML::_('image',sem_f006().'3132.png',null,array('border'=>'0','align'=>'absmiddle'))."</a>";        $knopfunten .= " <button class=\"button\" style=\"cursor:pointer;\" type=\"button\" onClick=\"window.open('".$href."');\"><img src=\"".sem_f006()."3116.png\" border=\"0\" align=\"absmiddle\">&nbsp;".JTEXT::_('SEM_1048')."</button>";      }      $knopfoben .= sem_f037(($art+2),'','','');      $knopfunten .= "&nbsp;".sem_f037(($art+2),'','','b');    }    if($config->get('sem_p024',2)==0 OR $config->get('sem_p024',2)==2) {      echo $knopfoben;    }    sem_f033();    $html = "";// ---------------------// Anzeige Kategoriekopf// ---------------------    $navioben1 = array();    if($art==0) {      if($catid==0) {        $headline = array(JTEXT::_('SEM_0027'),JTEXT::_('SEM_1001'));      } else {        $headline = sem_f012($catid);      }      $navioben1 = explode(" ",$config->get('sem_p053','SEM_NUMBER SEM_SEARCH SEM_CATEGORIES SEM_RESET'));    } elseif($art==1) {      $headline = array(JTEXT::_('SEM_1005'),JTEXT::_('SEM_1006'));      $navioben1 = explode(" ",$config->get('sem_p054','SEM_NUMBER SEM_SEARCH SEM_TYPES SEM_RESET'));    } elseif($art==2) {      $headline = array(JTEXT::_('SEM_1031'),JTEXT::_('SEM_1032'));      $navioben1 = explode(" ",$config->get('sem_p055','SEM_NUMBER SEM_SEARCH SEM_TYPES SEM_RESET'));    }    sem_f041($headline[0],$headline[1]);// ------------------------------------------------// Ausgabe der oberen Seitennavigation// ------------------------------------------------    $navioben2 = array('SEM_NUMBER','SEM_SEARCH','SEM_CATEGORIES','SEM_TYPES','SEM_RESET');    $navioben3 = array_diff($navioben2,$navioben1);    if(count($navioben1)>0 OR $navioben1[0]!="NULL") {      $html .= sem_f023(4)."<tr>";      foreach($navioben1 AS $el) {        switch($el) {          case "SEM_NUMBER":            $html .= sem_f022(JTEXT::_('SEM_0050')."&nbsp;".sem_f040(1,$limit),'d','l','','sem_nav');            break;          case "SEM_SEARCH":            $html .= sem_f022(JTEXT::_('SEM_0067').": <input class=\"sem_inputbox\" type=\"text\" name=\"search\" height=\"16\" size=\"15\" value=\"".$search."\" onChange=\"document.FrontForm.submit();\"/>",'d','c','','sem_nav');            break;          case "SEM_CATEGORIES":            $html .= sem_f022(JTEXT::_('SEM_0008').": ".$clist,'d','c','','sem_nav');            break;          case "SEM_TYPES":            $html .= sem_f022(JTEXT::_('SEM_1039').": ".$datelist,'d','c','','sem_nav');            break;          case "SEM_RESET":            $html .= sem_f022("<button class=\"button\" style=\"cursor:pointer;\" type=\"button\" onclick=\"document.FrontForm.dateid.value=1;document.FrontForm.catid.value=0;document.FrontForm.search.value='';document.FrontForm.limit.value=".$config->get('sem_p021','').";document.FrontForm.submit();\">".JTEXT::_('SEM_1036')."</button>",'d','r','','sem_nav');            break;        }      }      $html .= "</tr>".sem_f023('e');    }    $n = count($rows);    if($n<$total) {      $html .= $pageNav;    }// ---------------------------// Anzeige der einzelnen Kurse// ---------------------------    $html .= sem_f023(4);    if($n>0) {    // Schleife beginnen      for ($i=0, $n; $i < $n; $i++) {        $row = &$rows[$i];// Pruefung, ob Lehrgang buchbar        $buchopt = sem_f021($art, $row, $my->id);// Bild ausgeben        $html .= "<tr>";        $zusimage = "";        $zusbild = 0;        if($art==0) {          $linksbild = sem_f006()."2601.png";          if($my->id == $row->publisher) {            $linksbild = sem_f006()."2603.png";            $zusimage = sem_f006()."2607.png";          }          if($buchopt[0] == 2) {            $linksbild = sem_f006()."2602.png";            $zusimage = sem_f006()."2606.png";          }          $funktion = array(JTEXT::_('SEM_0014'),3);        } elseif($art==1) {          $linksbild = sem_f006()."2701.png";          $funktion = array(JTEXT::_('SEM_0014'),4);          $zusimage = sem_f006()."2606.png";        } elseif($art==2) {          $linksbild = sem_f006()."2801.png";          $funktion = array(JTEXT::_('SEM_0051'),9);          if($row->publisher==$my->id) {            $zusimage = sem_f006()."2607.png";          }        }        if($my->id==0) {          $zusimage = "";        }        if($row->cancelled==1) {          $linksbild = sem_f006()."2604.png";          $zusimage = sem_f006()."2200.png";        }        if($row->catimage!="") {          $linksbild = sem_f007(0).$row->catimage;          $zusbild = 1;        }        if($row->image!="" AND $config->get('sem_p032','')==1) {          $linksbild = sem_f007(1).$row->image;          $zusbild = 1;        }        $htxt = "<div style=\"position:relative;top:0px;left:0px;\"><a title=\"".$funktion[0]."\" href=\"javascript:auf('".$funktion[1]."','".$row->id."','');\"><img src=\"".$linksbild."\" border=\"0\">";        if($zusbild==1 AND $zusimage!="" AND $config->get('sem_p034',0)>0) {          $htxt .= "<div style=\"position:absolute;top:4px;left:4px;\"><img src=\"".$zusimage."\"></div>";        }        $htxt .= "</a></div>";        $html .= sem_f022($htxt,'d','l','',"sem_row");// Gebuehren anzeigen        $htxt = "";        if($row->fees>0) {          $gebuehr = sem_f044($row->fees);          $klasse = "sem_fees";          if($art==1 AND $buchopt[0]==2) {            if(count($buchopt[2])>0) {              if( $buchopt[2][0]->paid == 1) {                $klasse = "sem_fees_paid";              } else {                $klasse = "sem_fees_notpaid";              }              if($buchopt[2][0]->nrbooked>1) {                $gebuehr = sem_f044($row->fees*$buchopt[2][0]->nrbooked);                }            }          }          $htxt .= "<span class=\"".$klasse."\">".$config->get('sem_p017',JTEXT::_('SEM_0165'))." ".$gebuehr."</span>";                }// Beginn anzeigen        if($row->showbegin>0) {          if($row->cancelled == 1) {            $htxt .= "\n<span class=\"sem_cancelled\">".JTEXT::_('SEM_0103')."</span><span class=\"sem_date\"> (<del>".JHTML::_('date',$row->begin,$config->get('sem_p066',JTEXT::_('SEM_0166')),0)."</del>)</span><br />";          } else {            $htxt .= "\n<span class=\"sem_date\">".JHTML::_('date',$row->begin,$config->get('sem_p066',JTEXT::_('SEM_0166')),0)."</span><br />";          }        }// Titel anzeigen        $htxt .= "\n<a class=\"sem_title\" href=\"javascript:auf('".$funktion[1]."','".$row->id."','');\" title=\"".$funktion[0]."\">".$row->title."</a><br />";// Kurzbeschreibung anzeigen        $htxt .= "\n<span class=\"sem_shortdesc\">".$row->shortdesc."</span>";// Anmeldeschluss bzw. Buchungsdatum anzeigen        if($row->nrbooked<1) {          $htxt .= "<br />\n<span class=\"sem_cat\">".JTEXT::_('SEM_0133')."</span>";        } elseif($row->showbooked>0) {          if($buchopt[0] == 2) {            $htxt .= "<br />\n<span class=\"sem_cat\">".JTEXT::_('SEM_0032').": ".JHTML::_('date',$buchopt[2][0]->bookingdate,$config->get('sem_p066',JTEXT::_('SEM_0166')),0)."</span>";          } else {            if($row->cancelled==1) {              $htxt .= "<br />\n<span class=\"sem_cat\">".JTEXT::_('SEM_0011').": <del>".JHTML::_('date',$row->booked,$config->get('sem_p066',JTEXT::_('SEM_0166')),0)."</del></span>";            } else {              $htxt .= "<br />\n<span class=\"sem_cat\">".JTEXT::_('SEM_0011').": ".JHTML::_('date',$row->booked,$config->get('sem_p066',JTEXT::_('SEM_0166')),0)."</span>";            }          }        }        // Infozeile anzeigen        $gebucht = sem_f020($row);        if($config->get('sem_p014',0)==0) {          $htxt .= "<br />\n<span class=\"sem_cat\">".JTEXT::_('SEM_0008').": ".$row->category;          if($row->nrbooked>0) {            $htxt .= " - ".JTEXT::_('SEM_1044').": ".$gebucht->booked." - ".JTEXT::_('SEM_0053').": ".$buchopt[4]." - ".JTEXT::_('SEM_0058').": ".$row->hits;            $htxt .= "</span>";          }        }        $html .= sem_f022($htxt,'d','','98%',"sem_row");// Zertifikatdruck erlauben        if($config->get('sem_p003',0)>0 AND $art==1) {          if($buchopt[2][0]->certificated==1 AND $row->nrbooked>0) {            $htxt = sem_f037(1,$row->sid,'','');            $htbr = 30;          } else {            $htxt = "&nbsp;";            $htbr = "";          }          $html .= sem_f022($htxt,'h','',$htbr,"sem_row");        }// Anzeige der Teilnehmer erlauben        if(($config->get('sem_p030',0)==2 AND sem_f042()>1 AND $art==0) OR ($config->get('sem_p030',0)==1 AND $art==0) OR ($config->get('sem_p007',0)>0 AND sem_f042()>1 AND $art==1) OR ($config->get('sem_p031',0)>0 AND $art==2)) {          $htxt = "&nbsp";          if($row->nrbooked>0) {            $htxt = "<button class=\"button\" style=\"cursor:pointer;\" type=\"button\" onclick=\"auf(24,'".$row->id."','$art');\" title=\"".JTEXT::_('SEM_0035')."\">".$gebucht->booked."</button>";          }          $html .= sem_f022($htxt,'h','','30',"sem_row");        }// Bewertung erlauben        if($config->get('sem_p004',0)>0 AND $art>0) {          $htxt = "&nbsp";          if( $neudatum > $row->end AND $row->nrbooked>0) {            if($art==1) {              $htxt = sem_f035(sem_f006(),$row->id, $buchopt[2][0]->grade);            } elseif($art==2) {              $htxt = "<img src=\"".sem_f006()."240".$row->grade.".png\" alt=\"".JTEXT::_('SEM_0055')."\">";            }            $htbr = 30;          } else {            $htxt = "&nbsp;";            $htbr = "";          }          $html .= sem_f022($htxt,'h','',$htbr,"sem_row");        }// Ausgabe der Statusgrafik        if($config->get('sem_p025',2)>0) {          $htxt = "&nbsp;";          if($config->get('sem_p025',2)==1 AND $row->nrbooked>0) {            $htxt = "<img src=\"".sem_f006()."230".$buchopt[3].".png\" alt=\"".$buchopt[1]."\">";          } elseif ($config->get('sem_p025',2)==2 AND $row->nrbooked>0) {            $htxt = sem_f013($row->maxpupil,$buchopt[4],$buchopt[3]);          }          $html .= sem_f022($htxt,'d','c','24',"sem_row");        }         $html .= "</tr>";      }    } else {      $html .= "<tr>";      $html .= sem_f022(JTEXT::_('SEM_0062'),'h','','100%','sem_row');      $html .= "</tr>";    }    $html .= sem_f023('e');// ---------------------------------------// Ausgabe der Seitennavigation// ---------------------------------------    if(count($rows)<$total) {      $html .= $pageNav;    }// ---------------------------------------// Ausgabe der unsichtbaren Formularfelder// ---------------------------------------    if($art==0) {      $dots = array(JTEXT::_('SEM_0031'),JTEXT::_('SEM_0036'),JTEXT::_('SEM_0088'));    } elseif($art==1) {      $dots = array(JTEXT::_('SEM_0030'),JTEXT::_('SEM_0025'),JTEXT::_('SEM_0029'));    } elseif($art==2) {      $dots = array(JTEXT::_('SEM_0045'),JTEXT::_('SEM_0047'),JTEXT::_('SEM_0046'));    }// ---------------------------------------// Farbbeschreibungen anzeigen// ---------------------------------------    if(count($rows)>0 AND $config->get('sem_hide_ampel','')==0 AND $config->get('sem_p025',2)>0) {      $html .= sem_f029($dots[0],$dots[1],$dots[2]);    }// ---------------------------------// Anzeige Funktionsknoepfe unten// ---------------------------------    if($config->get('sem_p024',2)>0) {      $html .= sem_f023(4)."<tr>".sem_f022($knopfunten,'d','c','100%','sem_nav_d')."</tr>".sem_f023('e');    }// ---------------------------------------// Ausgabe der unsichtbaren Formularfelder// ---------------------------------------    foreach($navioben3 AS $el) {      switch($el) {        case "SEM_NUMBER":          $html .= "<input type=\"hidden\" name=\"limit\" value=\"".$limit."\">";          break;        case "SEM_SEARCH":          $html .= "<input type=\"hidden\" name=\"search\" value=\"".$search."\">";          break;        case "SEM_CATEGORIES":          $html .= "<input type=\"hidden\" name=\"catid\" value=\"".$catid."\">";          break;        case "SEM_TYPES":          $html .= "<input type=\"hidden\" name=\"dateid\" value=\"".$dateid."\">";          break;      }    }    $html .= sem_f014($art,"","","",$limitstart,0,"",-1);    echo $html;  }// ++++++++++++++++++++++++++++++++++++// +++ Anzeige der KursDetails      +++// ++++++++++++++++++++++++++++++++++++  function sem_g002($art,$row,$uid,$search,$catid,$limit,$limitstart,$dateid,$ueberschrift) {    global $mainframe;    $document = &JFactory::getDocument();    $database = &JFactory::getDBO();    $my = &JFactory::getuser();    $config = &JComponentHelper::getParams('com_seminar');    $neudatum = sem_f046();    JHTML::_('behavior.modal');    JHTML::_('behavior.tooltip');// ---------------------------------// Ist Kurs noch buchbar// ---------------------------------    $usrid = $my->id;    $modify = 26;    if($art>1) {      $usrid = $uid;    }    if($art>2) {      $modify = 29;    }    $buchopt = sem_f021($art,$row,$usrid);    $nametemp = "";    $htxt = 2;    if($art>2) {      if($usrid==0) {        $nametemp = sem_f011($row);      } else if($usrid>0) {        $nametemp = &JFactory::getuser($usrid);        $nametemp = $nametemp->name;      }      if($nametemp=="") {        $htxt = 2.2;      }    }    if($row->nrbooked==0) {      $htxt = 2.3;    }    $document->addCustomTag(sem_f027($htxt));    $bezahlt = 0;    if(count($buchopt[2])>0) {      if($buchopt[2][0]->paid==1) {        $bezahlt = 1;      }    }// ---------------------------------// Darf Kurs bearbeitet werden// ---------------------------------    $tempdis = " disabled";    if((($buchopt[0]==3 OR ($art==1 AND $config->get('sem_p022',"")==1 AND $bezahlt==0)) AND strtotime("$row->booked")-time()>=($config->get('sem_p018',0)*24*60*60) AND $art!=2) OR $art==3) {      $tempdis = "";    }// ---------------------------------// Anzeige Reiter // ---------------------------------    echo sem_f026(1);    $zurueck = array(0,1,0,23,23);    $knopfunten = "";    if($art==0 OR $art==2) {      $knopfunten = sem_f032(1);    } elseif($art==1) {      $knopfunten = sem_f032(2);    } elseif($art>2) {      $knopfunten = sem_f032(3);    }// ---------------------------------// Anzeige Funktionsknoepfe oben// ---------------------------------    $zusfeld = sem_f017($row);    $zfleer=1;    foreach($zusfeld[0] AS $el) {      if($el!="") {        $zfleer = 0;        break;      }    }    $gmapicon = "";// Zurueck-Knopf anzeigen    $knopfoben = "<a title=\"".JTEXT::_('SEM_1004')."\" href=\"javascript:auf('".$zurueck[$art]."','".$row->id."','');\"><img src=\"".sem_f006()."1032.png\" border=\"0\" align=\"absmiddle\"></a>";    $knopfunten .= " <button class=\"button\" style=\"cursor:pointer;\" type=\"button\" onclick=\"auf('".$zurueck[$art]."','".$row->id."','');\"><img src=\"".sem_f006()."1016.png\" border=\"0\" align=\"absmiddle\">&nbsp;".JTEXT::_('SEM_1004')."</button>";// Knopf fuer ICS-Datei anzeigen    if($config->get('sem_p052',0)>0) {      $knopfoben .= "<a title=\"".JTEXT::_('SEM_0130')."\" href=\"".sem_f004()."index2.php?s=".sem_f036()."&amp;option=".JRequest::getCmd('option')."&amp;task=33&amp;cid=".$row->id."\"><img src=\"".sem_f006()."3332.png\" border=\"0\" align=\"absmiddle\"></a>";      $knopfunten .= " <button onClick=\"document.location.href='".sem_f004()."index2.php?s=".sem_f036()."&amp;option=".JRequest::getCmd('option')."&amp;task=33&amp;cid=".$row->id."'\" class=\"button\" style=\"cursor:pointer;\" type=\"button\"><img src=\"".sem_f006()."3316.png\" border=\"0\" align=\"absmiddle\">&nbsp;".JTEXT::_('SEM_0130')."</button>";    }// Knopf fuer Nachricht anzeigen    if(($usrid!=$row->pub